import React, { useState, useEffect } from "react";

function CreateArea({ onAdd, editingNote, saveEdit }) {
  const [note, setNote] = useState({ title: "", content: "" });

  // Populate the form with the note being edited
  useEffect(() => {
    if (editingNote) {
      setNote(editingNote);
    } else {
      setNote({ title: "", content: "" });
    }
  }, [editingNote]);

  function handleChange(event) {
    const { name, value } = event.target;
    setNote((prevNote) => ({
      ...prevNote,
      [name]: value,
    }));
  }

  function handleSubmit(event) {
    event.preventDefault();
    if (editingNote) {
      saveEdit(note); // Save the edited note
    } else {
      onAdd(note); // Add a new note
    }
    setNote({ title: "", content: "" }); // Clear form
  }

  return (
    <form>
      <input
        name="title"
        placeholder="Title"
        value={note.title}
        onChange={handleChange}
      />
      <textarea
        name="content"
        placeholder="Take a note..."
        rows="3"
        value={note.content}
        onChange={handleChange}
      />
      <button onClick={handleSubmit}>
        {editingNote ? "Save" : "Add"}
      </button>
    </form>
  );
}

export default CreateArea;
